import React, { useEffect, useRef, useState, useCallback } from 'react';

function Mapa() {
  const [formData, setFormData] = useState({
    tipoImovel: 'Apartamento',
    bairro: 'ASA NORTE',
    quartos: '0',
    vagas: '10',
    metragem: '0',
    nrCluster: '5'
  });

  const [dadosAPI, setDadosAPI] = useState(null);
  const [dadosAPI2, setDadosAPI2] = useState(null);
  const [mapaHtml, setMapaHtml] = useState('');
  const [carregandoMapa, setCarregandoMapa] = useState(false);

  const mapSelectorRef = useRef(null);
  const mapOptionRef = useRef(null);

  // 1. Modificar updateField para apenas atualizar o estado
  const updateField = (field, value) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
    // REMOVIDO: buscarDados();
    // REMOVIDO: carregarMapa();
  };

  // Usar useCallback para memoizar as funções, evitando recriações desnecessárias
  const carregarMapa = useCallback(() => {
    const tipo = mapSelectorRef.current?.value || 'mapaAnuncio';
    const tamanho = mapOptionRef.current?.value || 'mapaCluster';
    const cluster = formData.nrCluster || '5'; // Usar formData atualizado

    setCarregandoMapa(true);
    const mapurl = `http://localhost:5000/mapa/carregar?tipo=${tipo}&cluster=${cluster}&tamanho=${tamanho}`;
    fetch(mapurl)
      .then((res) => res.text())
      .then(setMapaHtml)
      .catch(err => {
        console.error('Erro ao carregar o mapa:', err);
        alert("Erro ao carregar o mapa.");
      })
      .finally(() => setCarregandoMapa(false));
  }, [formData.nrCluster]); // Adicionar dependências relevantes para useCallback

  const buscarDados = useCallback(() => {
    const { tipoImovel, bairro, quartos, vagas, metragem, nrCluster } = formData; // Usar formData atualizado
    const url = `http://localhost:5000/imovel/venda?tipoImovel=${tipoImovel}&bairro=${bairro}&quartos=${quartos}&vagas=${vagas}&metragem=${metragem}&nrCluster=${nrCluster}`;
    const url2 = `http://localhost:5000/imovel/aluguel?tipoImovel=${tipoImovel}&bairro=${bairro}&quartos=${quartos}&vagas=${vagas}&metragem=${metragem}&nrCluster=${nrCluster}`;

    fetch(url)
      .then((res) => res.ok ? res.json() : res.json().then(err => { throw new Error(err.error || "Erro desconhecido") }))
      .then(setDadosAPI)
      .catch(err => console.error('Erro ao buscar dados de venda:', err));

    fetch(url2)
      .then((res) => res.ok ? res.json() : res.json().then(err => { throw new Error(err.error || "Erro desconhecido") }))
      .then(setDadosAPI2)
      .catch(err => console.error('Erro ao buscar dados de aluguel:', err));
  }, [formData]); // Adicionar formData como dependência para useCallback

  // 2. REMOVER o useEffect que dispara com formData
  // useEffect(() => {
  //   buscarDados();
  //   carregarMapa();
  // }, [formData]); // Este useEffect será removido ou comentado

  // useEffect para carregamento inicial (opcional, se desejar)
  useEffect(() => {
    buscarDados();
    carregarMapa();
  }, [buscarDados, carregarMapa]); // Executa na montagem e quando buscarDados/carregarMapa mudam (devido ao useCallback)


  const alterarClusterCopy = (valor) => {
    const novoCluster = valor === 'geral' ? '0' : valor.toString();
    // Apenas atualiza o estado. O carregamento do mapa será feito pelo botão de aplicar
    // ou se o carregarMapa nos dropdowns for mantido para reagir a esta mudança específica.
    setFormData((prev) => ({ ...prev, nrCluster: novoCluster }));
  };

  // 3. Criar a função para o botão de aplicar filtros
  const handleAplicarFiltros = () => {
    buscarDados();
    carregarMapa(); // Garante que o mapa também seja recarregado com os novos filtros/dados
  };

  return (
    <div className="tab-content">
      <div className="main-container">
        {/* Seus selects e inputs permanecem os mesmos */}
        <label htmlFor="tipoImovelCopia">Tipo de Imóvel:</label>
        <select
          value={formData.tipoImovel}
          onChange={(e) => updateField('tipoImovel', e.target.value)}
        >
          <option value="Apartamento">Apartamento</option>
          {/* Outras opções */}
        </select>

        <label>Bairro:</label>
        <select
          value={formData.bairro}
          onChange={(e) => updateField('bairro', e.target.value)}
        >
          <option value="ASA NORTE">ASA NORTE</option>
          <option value="ASA SUL">ASA SUL</option>
          <option value="NOROESTE">NOROESTE</option>
          <option value="SUDOESTE">SUDOESTE</option>
          <option value="AGUAS CLARAS">AGUAS CLARAS</option>
        </select>

        <label>Quartos:</label>
        <select
          value={formData.quartos}
          onChange={(e) => updateField('quartos', e.target.value)}
        >
          <option value="0">Todos</option>
          <option value="1">1</option>
          <option value="2">2</option>
          <option value="3">3</option>
          <option value="4">4+</option>
        </select>

        <label>Vagas:</label>
        <select
          value={formData.vagas}
          onChange={(e) => updateField('vagas', e.target.value)}
        >
          <option value="10">Todos</option>
          <option value="0">0</option>
          <option value="1">1+</option>
        </select>

        <label>Metragem (m²):</label>
        <input
          type="number"
          value={formData.metragem}
          onChange={(e) => updateField('metragem', e.target.value)}
          placeholder="Digite a metragem"
          min="0"
          step="1"
        />

        <div>
          <button type="button" onClick={() => alterarClusterCopy('geral')}>GERAL</button>
          <button type="button" onClick={() => alterarClusterCopy(2)}>ORIGINAL</button>
          <button type="button" onClick={() => alterarClusterCopy(5)}>SEMI-REFORMADO</button>
          <button type="button" onClick={() => alterarClusterCopy(8)}>REFORMADO</button>
        </div>

        {/* 4. Adicionar o botão de aplicar filtros */}
        <div style={{ marginTop: '1rem', marginBottom: '1rem' }}>
          <button type="button" onClick={handleAplicarFiltros} style={{ padding: '10px 20px', fontSize: '16px' }}>
            Aplicar Filtros e Atualizar
          </button>
        </div>
      </div>

      <div className="map-container" style={{ position: 'relative' }}>
        <div className="MapSelector">
          {/*
            A chamada onChange={carregarMapa} aqui fará com que o mapa
            se atualize imediatamente ao mudar o tipo de visualização do mapa.
            Isso pode ser o comportamento desejado. Se não, remova o onChange daqui
            e deixe que apenas o botão "Aplicar Filtros" recarregue o mapa.
          */}
          <select ref={mapSelectorRef} defaultValue="mapaAnuncio">
            <option value="mapaAnuncio">Mapa de anúncio</option>
            <option value="mapaM2">Mapa de valor de m2</option>
          </select>
          <select ref={mapOptionRef} defaultValue="mapaCluster">
            <option value="mapaCluster">Mapa Clusterizado</option>
            <option value="mapaCompleto">Mapa Completo</option>
          </select>
        </div>

        {carregandoMapa && (
          <div id="loading" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            <div id="loading-spinner"></div>
            <p>Carregando o mapa...</p>
          </div>
        )}
        {mapaHtml && (
          <iframe
            title="Mapa Dinâmico"
            srcDoc={mapaHtml}
            style={{
              width: '100%',
              height: '600px',
              border: 'none',
              marginTop: '1rem'
            }}
          />
        )}
      </div>
    </div>
  );
}

export default Mapa;
