import React, { useEffect, useState, useRef } from 'react';
import Logo61 from '../assets/img/LOGO 61 PNG (3).png';
import '../assets/css/footerPaginaUnica.css';
import '../assets/css/reportPaginaUnica.css';
import '../assets/css/stylesPaginaUnica.css';
import '../assets/css/chat.css';

function FormularioAnalise() {
  const [formData, setFormData] = useState({
    tipoImovel: 'Apartamento',
    bairro: 'ASA NORTE',
    quartos: '0',
    vagas: '10',
    metragem: '0',
    nrCluster: '5',
  });

  const [dadosAPI, setDadosAPI] = useState(null);
  const [dadosAPI2, setDadosAPI2] = useState(null);
  const [mapaHtml, setMapaHtml] = useState('');
  const [carregandoMapa, setCarregandoMapa] = useState(false);
  const [carregandoDados, setCarregandoDados] = useState(false); // Estado para loading geral dos dados
  const [erroDadosVenda, setErroDadosVenda] = useState(null); // Estado para erro na API de venda
  const [erroDadosAluguel, setErroDadosAluguel] = useState(null); // Estado para erro na API de aluguel

  const mapSelectorRef = useRef(null);
  const mapOptionRef = useRef(null);

  const updateField = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  useEffect(() => {
    buscarDados();
  }, [formData]);

  const buscarDados = () => {
    setCarregandoDados(true); // Inicia o loading geral
    setErroDadosVenda(null); // Limpa erro anterior de venda
    setErroDadosAluguel(null); // Limpa erro anterior de aluguel
    setDadosAPI(null); // Limpa dados anteriores para evitar mostrar dados antigos em caso de erro
    setDadosAPI2(null);

    const { tipoImovel, bairro, quartos, vagas, metragem, nrCluster } = formData;
    const url = `http://localhost:5000/imovel/venda?tipoImovel=${tipoImovel}&bairro=${bairro}&quartos=${quartos}&vagas=${vagas}&metragem=${metragem}&nrCluster=${nrCluster}`;
    const url2 = `http://localhost:5000/imovel/aluguel?tipoImovel=${tipoImovel}&bairro=${bairro}&quartos=${quartos}&vagas=${vagas}&metragem=${metragem}&nrCluster=${nrCluster}`;

    const fetchVenda = fetch(url)
      .then(async (res) => {
        if (!res.ok) {
          const errorData = await res.json().catch(() => ({})); // Tenta pegar corpo do erro
          throw new Error(errorData.error || `Erro HTTP ${res.status}`);
        }
        return res.json();
      })
      .then(data => setDadosAPI(data))
      .catch(err => {
        console.error('Erro ao buscar dados de venda:', err);
        setErroDadosVenda(`Erro análise venda: ${err.message}`);
      });

    const fetchAluguel = fetch(url2)
      .then(async (res) => {
        if (!res.ok) {
          const errorData = await res.json().catch(() => ({})); // Tenta pegar corpo do erro
          throw new Error(errorData.error || `Erro HTTP ${res.status}`);
        }
        return res.json();
      })
      .then(data => setDadosAPI2(data))
      .catch(err => {
        console.error('Erro ao buscar dados de aluguel:', err);
        setErroDadosAluguel(`Erro análise aluguel: ${err.message}`);
      });

    Promise.allSettled([fetchVenda, fetchAluguel]) // Usar allSettled para garantir que finally execute mesmo com rejeições
      .finally(() => {
        setCarregandoDados(false); // Finaliza o loading geral após ambas as requisições terminarem
      });
  };

  // Funções carregarMapa e alterarCluster permanecem iguais...
  const carregarMapa = () => {
    const tipo = mapSelectorRef.current?.value || 'mapaAnuncio';
    const cluster = formData.nrCluster || 5;
    const tamanho = mapOptionRef.current?.value || 'mapaCluster';

    setCarregandoMapa(true);

    fetch(`/carregar_mapa?tipo=${tipo}&cluster=${cluster}&tamanho=${tamanho}`)
      .then(res => res.text())
      .then(html => setMapaHtml(html))
      .catch(err => {
        console.error('Erro ao carregar o mapa:', err);
        alert('Erro ao carregar o mapa.');
      })
      .finally(() => setCarregandoMapa(false));
  };

  const alterarCluster = (valor) => {
    updateField('nrCluster', valor.toString());
  };


  return (
    <main>
      <div className="main-container">
        <form>
          {/* Seus selects e inputs permanecem os mesmos */}
          <label>Tipo de Imóvel:</label>
          <select value={formData.tipoImovel} onChange={e => updateField('tipoImovel', e.target.value)}>
            <option value="Apartamento">Apartamento</option>
          </select>

          <label>Bairro:</label>
          <select value={formData.bairro} onChange={e => updateField('bairro', e.target.value)}>
            <option value="ASA NORTE">ASA NORTE</option>
            <option value="ASA SUL">ASA SUL</option>
            <option value="NOROESTE">NOROESTE</option>
            <option value="SUDOESTE">SUDOESTE</option>
            <option value="AGUAS CLARAS">AGUAS CLARAS</option>
          </select>

          <label>Quartos:</label>
          <select value={formData.quartos} onChange={e => updateField('quartos', e.target.value)}>
            <option value="0">Todos</option>
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
            <option value="4">4+</option>
          </select>

          <label>Vagas:</label>
          <select value={formData.vagas} onChange={e => updateField('vagas', e.target.value)}>
            <option value="10">Todos</option>
            <option value="0">0</option>
            <option value="1">1+</option>
          </select>

          <label>Metragem (m²):</label>
          <input
            type="number"
            value={formData.metragem}
            onChange={e => updateField('metragem', e.target.value)}
            onKeyDown={e => {
              if (e.key === 'Enter') {
                e.preventDefault();
              }
            }}
          />

          <div style={{ marginTop: 10 }}>
            <button type="button" onClick={() => alterarCluster(2)}>ORIGINAL</button>
            <button type="button" onClick={() => alterarCluster(5)}>SEMI-REFORMADO</button>
            <button type="button" onClick={() => alterarCluster(8)}>REFORMADO</button>
          </div>

          {/* Exibição condicional: Loading, Erro ou Dados */} 
          {carregandoDados ? (
            <div style={{ textAlign: 'center', margin: '20px' }}>
              <p>Carregando dados da análise...</p>
            </div>
          ) : (
            <ul className="lista-com-imagem">
              <li className="negrito">
                <strong>Valor de M² de Venda:</strong> 
                {erroDadosVenda ? 
                  <span style={{ color: 'red' }}> {erroDadosVenda}</span> : 
                  ` R$ ${dadosAPI?.valorM2Venda != null ? dadosAPI.valorM2Venda.toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 }) : '-'} /m²`
                }
              </li>
              <li className="negrito">
                <strong>Valor de Venda Nominal:</strong> 
                {erroDadosVenda ? 
                  <span style={{ color: 'red' }}> {erroDadosVenda}</span> : 
                  ` R$ ${dadosAPI?.valorVendaNominal != null ? dadosAPI?.valorVendaNominal.toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 }) : '-'}`
                }
              </li>
              <li className="negrito">
                <strong>Valor de M² de Locação:</strong> 
                {erroDadosAluguel ? 
                  <span style={{ color: 'red' }}> {erroDadosAluguel}</span> : 
                  ` R$ ${dadosAPI2?.valorM2Aluguel != null ? dadosAPI2?.valorM2Aluguel.toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 }) : '-'} /m²`
                }
              </li>
              <li className="negrito">
                <strong>Valor de Locação Nominal:</strong> 
                {erroDadosAluguel ? 
                  <span style={{ color: 'red' }}> {erroDadosAluguel}</span> : 
                  ` R$ ${dadosAPI2?.valorAluguelNominal != null ? dadosAPI2?.valorAluguelNominal.toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 }) : '-'}`
                }
              </li>
            </ul>
          )}

          <div className="container">
            <label htmlFor="nrCluster" className="TituloCluster">Número de Clusters (1 a 9):</label>
            <input
              type="range"
              min="1"
              id="nrCluster"
              name="nrCluster"
              max="9"
              value={formData.nrCluster}
              onChange={(e) => updateField('nrCluster', e.target.value)}
            />
            <output id="outCluster">{formData.nrCluster}</output>
            <img src={Logo61} alt="Imagem sobre o campo" className="imagem-sobreposta" />
          </div>
        </form>

        {/* O loading do mapa já existe e parece correto */}
        <div style={{ marginTop: '20px' }}>
          {carregandoMapa && (
            <div style={{ textAlign: 'center', margin: '20px' }}>Carregando mapa...</div>
          )}
          <div dangerouslySetInnerHTML={{ __html: mapaHtml }} style={{ display: carregandoMapa ? 'none' : 'block' }} />
        </div>
      </div>
    </main>
  );
}

export default FormularioAnalise;
