// LinkReporteImovel.jsx
import React, { useMemo, useState } from "react";

export default function LinkReporteImovel({
  baseUrl = "https://www.inteligencia61imoveis.com.br/api/reporteImovel",
  paramName = "codgigo", // conforme solicitado
  autoFocus = true,
}) {
  const [codigo, setCodigo] = useState("");

  const link = useMemo(() => {
    const c = String(codigo || "").trim();
    if (!c) return "";
    const qs = new URLSearchParams({ [paramName]: c }).toString();
    return `${baseUrl}?${qs}`;
  }, [codigo, baseUrl, paramName]);

  const onSubmit = (e) => {
    e.preventDefault();
    if (link) window.open(link, "_blank", "noopener,noreferrer");
  };

  const onCopy = async () => {
    if (!link) return;
    try {
      await navigator.clipboard.writeText(link);
      // opcional: feedback discreto no título do botão
      const btn = document.getElementById("lr-copy");
      if (btn) {
        const old = btn.textContent;
        btn.textContent = "Copiado";
        setTimeout(() => (btn.textContent = old), 1200);
      }
    } catch (_) {}
  };

  return (
    <section className="lr-card">
      <form onSubmit={onSubmit} className="lr-row">
        <label htmlFor="lr-codigo" className="lr-label">Código do imóvel</label>
        <input
          id="lr-codigo"
          className="lr-input"
          placeholder="Ex.: 28"
          value={codigo}
          onChange={(e) => setCodigo(e.target.value)}
          autoFocus={autoFocus}
          autoComplete="off"
          inputMode="numeric"
        />
        <button type="submit" className="lr-btn" disabled={!link}>
          Abrir API ↗
        </button>
        <button
          type="button"
          id="lr-copy"
          className="lr-btn ghost"
          onClick={onCopy}
          disabled={!link}
        >
          Copiar link
        </button>
      </form>

      <div className="lr-link-wrap">
        <span className="lr-link-label">Link gerado:</span>
        {link ? (
          <a className="lr-link" href={link} target="_blank" rel="noreferrer">
            {link}
          </a>
        ) : (
          <span className="lr-link placeholder">—</span>
        )}
      </div>
    </section>
  );
}
