// ============================
// File: ReporteImovelWidget.jsx (React, JS)
// Drop-in, sem header/footer, visual limpo e moderno.
// Uso:
//   import ReporteImovelWidget from "./ReporteImovelWidget";
//   // Se usa CRA com proxy no package.json, deixe apiBase="/api/reporteImovel"
//   <ReporteImovelWidget defaultCodigo="28" apiBase="/api/reporteImovel" />
// ============================

import React, { useCallback, useEffect, useMemo, useRef, useState } from "react";

export default function ReporteImovelWidget({
  apiBase = "/api/reporteImovel", // melhor experiência em dev se usar proxy do CRA
  defaultCodigo = "",
  autoFocus = true,
}) {
  const [codigo, setCodigo] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [jsonData, setJsonData] = useState(null);
  const [textData, setTextData] = useState("");
  const [pdfUrl, setPdfUrl] = useState("");
  const abortRef = useRef(null);
  const lastPdfUrlRef = useRef("");

  const endpointFor = useCallback(
    (cod) => `${apiBase}?${new URLSearchParams({ codigo: String(cod).trim() })}`,
    [apiBase]
  );

  const revokeLastPdf = () => {
    if (lastPdfUrlRef.current) {
      URL.revokeObjectURL(lastPdfUrlRef.current);
      lastPdfUrlRef.current = "";
    }
  };

  const isPdfBlob = async (blob) => {
    if (blob.type && blob.type.includes("pdf")) return true;
    try {
      const head = await blob.slice(0, 5).text();
      return head.startsWith("%PDF-");
    } catch (_) {
      return false;
    }
  };

  const resetState = () => {
    setJsonData(null);
    setTextData("");
    setPdfUrl("");
    revokeLastPdf();
  };

  const fetchData = useCallback(
    async (cod) => {
      const trimmed = String(cod ?? codigo).trim();
      if (!trimmed) {
        setError("Informe um código.");
        resetState();
        return;
      }

      setError("");
      setLoading(true);
      resetState();

      try {
        if (abortRef.current) abortRef.current.abort();
        const controller = new AbortController();
        abortRef.current = controller;

        const res = await fetch(endpointFor(trimmed), {
          method: "GET",
          headers: { Accept: "*/*" },
          signal: controller.signal,
        });

        if (!res.ok) {
          const fallback = await res.text().catch(() => "");
          throw new Error(`HTTP ${res.status} - ${res.statusText}${fallback ? ` | ${fallback}` : ""}`);
        }

        const ct = res.headers.get("content-type") || "";
        const blob = await res.blob();

        if (ct.includes("application/json")) {
          const json = JSON.parse(await blob.text());
          setJsonData(json);
        } else if (ct.includes("application/pdf") || (await isPdfBlob(blob))) {
          const url = URL.createObjectURL(blob);
          setPdfUrl(url);
          lastPdfUrlRef.current = url;
        } else {
          const text = await blob.text();
          // Algumas instâncias retornam PDF com ct genérico; tentamos uma última vez detectar
          if (text.startsWith("%PDF-")) {
            const buf = new Blob([text], { type: "application/pdf" });
            const url = URL.createObjectURL(buf);
            setPdfUrl(url);
            lastPdfUrlRef.current = url;
          } else {
            setTextData(text);
          }
        }
      } catch (err) {
        if (err?.name === "AbortError") return; // cancelada
        setError(err?.message || "Erro na requisição");
      } finally {
        setLoading(false);
      }
    },
    [codigo, endpointFor]
  );

  useEffect(() => () => revokeLastPdf(), []);

  // Lê ?codigo= da URL ou usa defaultCodigo
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const fromUrl = params.get("codigo");
    if (fromUrl) {
      setCodigo(fromUrl);
      fetchData(fromUrl);
    } else if (defaultCodigo) {
      setCodigo(defaultCodigo);
      fetchData(defaultCodigo);
    } else if (autoFocus) {
      const el = document.querySelector("#riw-codigo-input");
      if (el) el.focus();
    }
  }, [defaultCodigo, autoFocus, fetchData]);

  const onSubmit = (e) => {
    e.preventDefault();
    fetchData();
  };

  const prettyJson = useMemo(() => {
    try {
      return jsonData ? JSON.stringify(jsonData, null, 2) : "";
    } catch {
      return "";
    }
  }, [jsonData]);

  const onDownload = () => {
    if (jsonData) {
      const blob = new Blob([JSON.stringify(jsonData, null, 2)], { type: "application/json" });
      const url = URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `reporteImovel_${(codigo || "resultado").trim()}.json`;
      document.body.appendChild(a);
      a.click();
      a.remove();
      URL.revokeObjectURL(url);
    } else if (pdfUrl) {
      const a = document.createElement("a");
      a.href = pdfUrl;
      a.download = `reporteImovel_${(codigo || "resultado").trim()}.pdf`;
      document.body.appendChild(a);
      a.click();
      a.remove();
    } else if (textData) {
      const blob = new Blob([textData], { type: "text/plain;charset=utf-8" });
      const url = URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `reporteImovel_${(codigo || "resultado").trim()}.txt`;
      document.body.appendChild(a);
      a.click();
      a.remove();
      URL.revokeObjectURL(url);
    }
  };

  return (
    <section className="riw2-card" aria-live="polite">
      <form className="riw2-form" onSubmit={onSubmit}>
        <label htmlFor="riw-codigo-input" className="riw2-label">Código do imóvel</label>
        <div className="riw2-row">
          <input
            id="riw-codigo-input"
            className="riw2-input"
            placeholder="Ex.: 28"
            value={codigo}
            onChange={(e) => setCodigo(e.target.value)}
            inputMode="numeric"
            autoComplete="off"
          />
          <button type="submit" className="riw2-btn" disabled={loading}>
            {loading ? "Consultando..." : "Consultar"}
          </button>
        </div>
      </form>

      {error && (
        <div className="riw2-alert" role="alert">{error}</div>
      )}

      {loading && (
        <div className="riw2-loading">
          <span className="riw2-spinner" aria-hidden />
          <span>Carregando...</span>
        </div>
      )}

      {!loading && (jsonData || pdfUrl || textData) && (
        <div className="riw2-result">
          <div className="riw2-actions">
            <button type="button" className="riw2-chip" onClick={onDownload}>
              {pdfUrl ? "Baixar PDF" : jsonData ? "Baixar JSON" : "Baixar TXT"}
            </button>
            <a className="riw2-chip" href={endpointFor(codigo)} target="_blank" rel="noreferrer">
              Abrir API ↗
            </a>
          </div>

          {pdfUrl && (
            <div className="riw2-pdfwrap">
              <iframe title="Relatório em PDF" className="riw2-pdf" src={pdfUrl} />
            </div>
          )}

          {jsonData && (
            <pre className="riw2-pre">{prettyJson}</pre>
          )}

          {textData && !pdfUrl && !jsonData && (
            <pre className="riw2-pre">{textData}</pre>
          )}
        </div>
      )}
    </section>
  );
}
