import React, { useState } from 'react';

function Formulario() {
  const [metragem, setMetragem] = useState(0);
  const [cluster, setCluster] = useState(5);

  const submitOnChange = () => {
    document.getElementById("tipoImovelCopia").value = document.getElementById("tipoImovel").value;
    document.getElementById("bairroCopia").value = document.getElementById("bairro").value;
    document.getElementById("quartosCopia").value = document.getElementById("quartos").value ;
    document.getElementById("vagasCopia").value = document.getElementById("vagas").value;
    document.getElementById("metragemCopia").value = document.getElementById("metragem").value;

    const formData = new FormData(document.getElementById('formularioAnalise'));
    const formObject = Object.fromEntries(formData.entries());

    fetch('http://localhost:5000/api/analise/imovel?tipoImovel=Apartamento&bairro=ASA SUL&quartos=3&vagas=1&metragem=100&nrCluster=5', {
        method: 'GET'
    })
    .then(response => {
        if (response.ok) {
            return response.json();
        } else {
            return response.json().then(err => { throw new Error(err.error || "Erro desconhecido"); });
        }
    })
    .then(data => {
        // Animação para atualização suave dos valores
        animateUpdate('vlrM2Venda', `Valor de M² de Venda: ${data.valorM2Venda}`);
        animateUpdate('vlrVendaNominal', `Valor de Venda Nominal: ${data.valorVendaNominal}`);
        animateUpdate('mtrMediaVenda', `Metragem Média de Venda: ${data.metragemMediaVenda}`);
        animateUpdate('coeficienteVar', `Coeficiente de Variação de Venda: ${data.coeficienteVariacaoVenda}`);
        animateUpdate('tamAmostra', `Tamanho da Amostra de Venda: ${data.tamanhoAmostraVenda}`);
        animateUpdate('vlrM2Loc', `Valor de M² de Locação: ${data.valorM2Locacao}`);
        animateUpdate('vlrLocacaoNominal', `Valor de Locação Nominal: ${data.valorLocacaoNominal}`);
        animateUpdate('mtrMediaLoc', `Metragem Média de Locação: ${data.metragemMediaLocacao}`);
        animateUpdate('coeficienteVarLoc', `Coeficiente de Variação de Locação: ${data.coeficienteVariacaoLocacao}`);
        animateUpdate('tamAmostraLoc', `Tamanho da Amostra de Locação: ${data.tamanhoAmostraLocacao}`);
        animateUpdate('Rentabilidade', `Rentabilidade Média: ${data.rentabilidadeMedia}`);
        
        // Atualiza o mapa chamando a função de carregamento do mapa
        carregarMapa(document.getElementById('map-selector').value,document.getElementById('outCluster').value, document.getElementById('mapOption').value);
    })
    .catch(error => {
        console.error('Erro:', error);
    });
  };

  const alterarCluster = (valor) => {
    if (valor === "geral") {
      carregarMapa(document.getElementById('map-selector').value,0, document.getElementById('mapOption').value);
    }else{
        document.getElementById('nrCluster').value = parseInt(valor); // Atualiza o valor do campo de entrada
        document.getElementById('outCluster').value = parseInt(valor); // Atualiza o valor do output
        submitOnChange()
    }
  };

  const carregarMapa = (tipoMapa, cluster, tamanho_mapa) => {
      // Exibir o spinner antes de carregar o mapa
      const clusterTemp = cluster ?? 5;
      const clusterElement = document.getElementById('nrCluster'); // Assumindo que o elemento tem o ID 'cluster'
      const clusterOutput = document.getElementById('outCluster');

      if (clusterElement) {
          clusterElement.value = clusterTemp;
          clusterOutput.value = clusterTemp // Atualiza o valor exibido para o cluster selecionado
      }
      document.getElementById('loading').style.display = 'flex'; 
      document.getElementById('map').style.display = 'none'; 

      fetch(`/carregar_mapa?tipo=${tipoMapa}&cluster=${clusterTemp}&tamanho=${tamanho_mapa}`)
          .then(response => response.text())
          .then(data => {
              // Inserir o mapa no contêiner e esconder o spinner de carregamento
              document.getElementById('map').innerHTML = data;
              document.getElementById('loading').style.display = 'none';  // Esconder o spinner
              document.getElementById('map').style.display = 'block';     // Mostrar o mapa
          })
          .catch(error => {
              console.error('Erro ao carregar o mapa:', error);
              alert("Erro ao carregar o mapa.");
          });
  }

  // Animação suave de atualização
  const animateUpdate = (elementId, newText) => {
      const element = document.getElementById(elementId);
      element.style.opacity = 0;
      setTimeout(() => {
          element.innerText = newText;
          element.style.opacity = 1;
      }, 300);
  }

  document.addEventListener('DOMContentLoaded', function() {
      submitOnChange();
      carregarMapa(); // Carregar o mapa inicialmente ao carregar a página
  });

  document.getElementById('map-selector').addEventListener('change', function() {
      carregarMapa(document.getElementById('map-selector').value,document.getElementById('outCluster').value, document.getElementById('mapOption').value);
  });

  document.getElementById('mapOption').addEventListener('change', function() {
      carregarMapa(document.getElementById('map-selector').value,document.getElementById('outCluster').value, document.getElementById('mapOption').value);
  });

  const changeTab = (tabId) => {
      // Esconde todas as seções
      document.querySelectorAll('.tab-content').forEach(tab => tab.classList.remove('active-content'));
      // Mostra apenas a seção correspondente
      document.getElementById(tabId).classList.add('active-content');

      // Atualiza a aba ativa
      document.querySelectorAll('.tab').forEach(tab => tab.classList.remove('active-tab'));
      event.target.classList.add('active-tab');
  }

  const submitOnChangeInCopy = () => {
      // Copiar valores dos selects
      document.getElementById("tipoImovel").value = document.getElementById("tipoImovelCopia").value;
      document.getElementById("bairro").value = document.getElementById("bairroCopia").value;
      document.getElementById("quartos").value = document.getElementById("quartosCopia").value;
      document.getElementById("vagas").value = document.getElementById("vagasCopia").value;
      
      // Copiar valores dos inputs
      document.getElementById("metragem").value = document.getElementById("metragemCopia").value;


      // Chama a função para processar as mudanças
      submitOnChange();
  }

  const alterarClusterCopy = (valor) => {
      if (valor === "geral") {
          carregarMapa(document.getElementById('map-selector').value,0, document.getElementById('mapOption').value);
      }else{
          document.getElementById('nrCluster').value = parseInt(valor); // Atualiza o valor do campo de entrada
          document.getElementById('outCluster').value = parseInt(valor); // Atualiza o valor do output
          submitOnChange()
      }
  }

  return (
    <div className="tab-content active-content" id="formulario">
      <div className="main-container">
        <form id="formularioAnalise" method="post">
          <div id="tipoImovel-section">
            <label htmlFor="tipoImovel">Tipo de Imóvel:</label>
            <select id="tipoImovel" name="tipoImovel" required onChange={submitOnChange}>
              <option value="Apartamento">Apartamento</option>
            </select>
          </div>

          <div id="bairro-section">
            <label htmlFor="bairro">Bairro:</label>
            <select id="bairro" name="bairro" required onChange={submitOnChange}>
              <option value="ASA NORTE">ASA NORTE</option>
              <option value="ASA SUL">ASA SUL</option>
              <option value="NOROESTE">NOROESTE</option>
              <option value="SUDOESTE">SUDOESTE</option>
              <option value="AGUAS CLARAS">AGUAS CLARAS</option>
            </select>
          </div>

          <div id="quartos-section">
            <label htmlFor="quartos">Quartos:</label>
            <select id="quartos" name="quartos" required onChange={submitOnChange}>
              <option value="0">Todos</option>
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
              <option value="4">4+</option>
            </select>
          </div>

          <div id="vagas-section">
            <label htmlFor="vagas">Vagas:</label>
            <select id="vagas" name="vagas" required onChange={submitOnChange}>
              <option value="10">Todos</option>
              <option value="0">0</option>
              <option value="1">1+</option>
            </select>
          </div>

          <div id="nome">
            <label htmlFor="metragem">Metragem (m²):</label>
            <input
              type="number"
              id="metragem"
              name="metragem"
              step="1"
              min="0"
              placeholder="Digite a metragem"
              value={metragem}
              onChange={(e) => {
                setMetragem(e.target.value);
                submitOnChange();
              }}
              onKeyDown={(e) => {
                if (e.key === 'Enter') {
                  e.preventDefault();
                  submitOnChange();
                }
              }}
            />
            <button type="button" onClick={() => alterarCluster(2)}>ORIGINAL</button>
            <button type="button" onClick={() => alterarCluster(5)}>SEMI-REFORMADO</button>
            <button type="button" onClick={() => alterarCluster(8)}>REFORMADO</button>
          </div>

          <label htmlFor="nrCluster">Número de Clusters (1 a 9):</label>
          <input
            type="range"
            id="nrCluster"
            name="nrCluster"
            min="1"
            max="9"
            value={cluster}
            onInput={(e) => {
              setCluster(e.target.value);
              submitOnChange();
            }}
          />
          <output id="outCluster">{cluster}</output>

          <ul>
            <li id="vlrM2Venda"><strong>Valor de M² de Venda:</strong></li>
            <li id="vlrVendaNominal"><strong>Valor de Venda Nominal:</strong></li>
            <li id="mtrMediaVenda"><strong>Metragem Média de Venda:</strong></li>
            <li id="coeficienteVar"><strong>Coeficiente de Variação de Venda:</strong></li>
            <li id="tamAmostra"><strong>Tamanho da Amostra de Venda:</strong></li>
            <li id="vlrM2Loc"><strong>Valor de M² de Locação:</strong></li>
            <li id="vlrLocacaoNominal"><strong>Valor de Locação Nominal:</strong></li>
            <li id="mtrMediaLoc"><strong>Metragem Média de Locação:</strong></li>
            <li id="coeficienteVarLoc"><strong>Coeficiente de Variação de Locação:</strong></li>
            <li id="tamAmostraLoc"><strong>Tamanho da Amostra de Locação:</strong></li>
            <li id="Rentabilidade"><strong>Rentabilidade Média:</strong></li>
          </ul>
        </form>
      </div>
    </div>
  );
}

export default Formulario;
