// ============================
// File: ReporteImovelWidget.jsx
// Usage:
//   import ReporteImovelWidget from "./ReporteImovelWidget";
//   import "./ReporteImovelWidget.css";
//   <ReporteImovelWidget defaultCodigo="3346" />
// ============================

import React, { useCallback, useEffect, useMemo, useRef, useState } from "react";

export default function ReporteImovelWidget({
  apiBase = "https://www.inteligencia61imoveis.com.br/api/reporteImovel",
  defaultCodigo = "",
  autoFocus = true,
}) {
  const [codigo, setCodigo] = useState("");
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const abortRef = useRef(null);

  const endpointFor = useCallback(
    (cod) => `${apiBase}?${new URLSearchParams({ codigo: String(cod).trim() })}`,
    [apiBase]
  );

  const fetchData = useCallback(
    async (cod) => {
      const trimmed = String(cod || codigo).trim();
      if (!trimmed) {
        setError("Informe um código.");
        setData(null);
        return;
      }
      setError("");
      setLoading(true);
      setData(null);

      try {
        // Cancela requisição anterior, se houver
        if (abortRef.current) abortRef.current.abort();
        const controller = new AbortController();
        abortRef.current = controller;

        const res = await fetch(endpointFor(trimmed), {
          method: "GET",
          headers: { Accept: "application/json" },
          signal: controller.signal,
        });

        if (!res.ok) {
          const text = await res.text().catch(() => "");
          throw new Error(`HTTP ${res.status} - ${res.statusText} ${text ? "| " + text : ""}`);
        }

        // Tenta interpretar como JSON; se falhar, mantém texto bruto
        const contentType = res.headers.get("content-type") || "";
        if (contentType.includes("application/json")) {
          const json = await res.json();
          setData(json);
        } else {
          const text = await res.text();
          setData({ raw: text });
        }
      } catch (err) {
        if (err?.name === "AbortError") return; // requisição cancelada
        setError(err?.message || "Erro na requisição");
      } finally {
        setLoading(false);
      }
    },
    [codigo, endpointFor]
  );

  // Lê ?codigo= da URL ou usa defaultCodigo
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const fromUrl = params.get("codigo");
    if (fromUrl) {
      setCodigo(fromUrl);
      fetchData(fromUrl);
    } else if (defaultCodigo) {
      setCodigo(defaultCodigo);
      fetchData(defaultCodigo);
    } else if (autoFocus) {
      const el = document.querySelector("#riw-codigo-input");
      if (el) el.focus();
    }
  }, [defaultCodigo, autoFocus, fetchData]);

  const onSubmit = (e) => {
    e.preventDefault();
    fetchData();
  };

  const pretty = useMemo(() => {
    try {
      return data ? JSON.stringify(data, null, 2) : "";
    } catch (_) {
      return String(data);
    }
  }, [data]);

  const handleDownload = () => {
    const blob = new Blob([pretty || "{}"], { type: "application/json;charset=utf-8" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `reporteImovel_${(codigo || "resultado").trim()}.json`;
    document.body.appendChild(a);
    a.click();
    a.remove();
    URL.revokeObjectURL(url);
  };

  return (
    <section className="riw-card" aria-live="polite">
      <header className="riw-header">
        <h2 className="riw-title">Relatório do Imóvel</h2>
        <p className="riw-subtitle">Consulta via GET em <code>/api/reporteImovel?codigo=...</code></p>
      </header>

      <form className="riw-form" onSubmit={onSubmit}>
        <label htmlFor="riw-codigo-input" className="riw-label">
          Código do imóvel
        </label>
        <div className="riw-inputrow">
          <input
            id="riw-codigo-input"
            className="riw-input"
            placeholder="Ex.: 3346"
            value={codigo}
            onChange={(e) => setCodigo(e.target.value)}
            inputMode="numeric"
            autoComplete="off"
          />
          <button type="submit" className="riw-button" disabled={loading}>
            {loading ? "Consultando..." : "Consultar"}
          </button>
        </div>
      </form>

      {error && (
        <div className="riw-alert riw-alert-error" role="alert">
          <strong>Erro:</strong> {error}
        </div>
      )}

      {loading && (
        <div className="riw-loading">
          <span className="riw-spinner" aria-hidden />
          <span>Carregando...</span>
        </div>
      )}

      {!loading && data && (
        <div className="riw-result">
          <div className="riw-resultbar">
            <span className="riw-resultlabel">Resultado</span>
            <div className="riw-actions">
              <button type="button" className="riw-action" onClick={handleDownload}>
                Baixar JSON
              </button>
              <a
                className="riw-action"
                href={endpointFor(codigo)}
                target="_blank"
                rel="noreferrer"
                title="Abrir resposta em nova aba"
              >
                Abrir API ↗
              </a>
            </div>
          </div>
          <pre className="riw-pre" data-testid="riw-json">
            {pretty}
          </pre>
        </div>
      )}

      <footer className="riw-footer">
        <small>
          Dica: também é possível acessar diretamente
          {" "}
          <code>{apiBase}?codigo={"{codigo}"}</code>.
        </small>
      </footer>
    </section>
  );
}


